mapper.select:
SELECT R.*, C.CUSTOMER_NAME, C.CUSTOMER_TYPE, LM.TRACE_IDS FROM meter_record R LEFT JOIN CUSTOMERS C ON R.CUSTOMER_ID = C.ID LEFT JOIN METERS M ON R.METER_ID = M.ID LEFT JOIN LOCATION_METER LM ON R.METER_ID=LM.METER_ID
R.DELETED=0 AND LM.DELETED!=1 AND C.DELETED=0 AND M.DELETED=0
AND R.PRE_READ IS NOT NULL
AND R.CUSTOMER_ID = #{customerId}
AND ( C.CUSTOMER_CODE LIKE CONCAT(CONCAT('%',#{searchCond}),'%')
OR C.CUSTOMER_NAME LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR C.CUSTOMER_MOBILE LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR C.CUSTOMER_TEL LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR C.ROOM LIKE CONCAT('%', CONCAT(#{searchCond}, '%'))
OR M.METER_NO LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR M.STEEL_SEAL_NO LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR M.DESCRIPTION LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR M.PLACE LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) )
AND LM.TRACE_IDS LIKE CONCAT(#{traceIds},'%')
AND R.PERIOD=#{period}
AND R.CURR_DATE >= to_date(#{startDate}, 'yyyy-MM-dd HH24:Mi:ss')
AND R.CURR_DATE <= to_date(#{endDate}, 'yyyy-MM-dd HH24:Mi:ss')
AND R.READ_TYPE = #{readType}
AND R.STATUS = #{meterRecordStatus}
AND R.IS_PART_WATER = #{isPartWater}
AND R.OPERATOR_ID = #{operatorId}
AND R.CURR_AMOUNT > 0
AND R.CURR_AMOUNT=0
AND R.CURR_AMOUNT < 0
ORDER BY R.PERIOD DESC, R.CUSTOMER_ID DESC, R.ID DESC

SELECT SUM(R.CURR_AMOUNT) AS TOTAL_WATER_AMOUNT FROM meter_record R LEFT JOIN CUSTOMERS C ON R.CUSTOMER_ID = C.ID LEFT JOIN METERS M ON R.METER_ID = M.ID LEFT JOIN LOCATION_METER LM ON R.METER_ID=LM.METER_ID
R.DELETED=0 AND LM.DELETED!=1 AND C.DELETED=0 AND M.DELETED=0
AND R.PRE_READ IS NOT NULL
AND R.CUSTOMER_ID = #{customerId}
AND ( C.CUSTOMER_CODE LIKE CONCAT(CONCAT('%',#{searchCond}),'%')
OR C.CUSTOMER_NAME LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR C.CUSTOMER_MOBILE LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR C.CUSTOMER_TEL LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR C.ROOM LIKE CONCAT('%', CONCAT(#{searchCond}, '%'))
OR M.METER_NO LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR M.STEEL_SEAL_NO LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR M.DESCRIPTION LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR M.PLACE LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) )
AND LM.TRACE_IDS LIKE CONCAT(#{traceIds},'%')
AND R.PERIOD=#{period}
AND R.CURR_DATE >= to_date(#{startDate}, 'yyyy-MM-dd HH24:Mi:ss')
AND R.CURR_DATE <= to_date(#{endDate}, 'yyyy-MM-dd HH24:Mi:ss')
AND R.READ_TYPE = #{readType}
AND R.STATUS = #{meterRecordStatus}
AND R.IS_PART_WATER = #{isPartWater}
AND R.OPERATOR_ID = #{operatorId}
AND R.CURR_AMOUNT > 0
AND R.CURR_AMOUNT=0
AND R.CURR_AMOUNT < 0
ORDER BY R.PERIOD DESC, R.CUSTOMER_ID DESC, R.ID DESC

SELECT * FROM ( SELECT TO_CHAR ( R.ID ) AS RECORD_IDS, TO_CHAR ( R.METER_ID ) AS METER_IDS, R.CUSTOMER_ID AS CUSTOMER_ID, R.PERIOD AS PERIOD, R.IS_ADD_SUB_WATER AS IS_ADD_SUB_WATER, R.IS_PART_WATER AS IS_PART_WATER, C.IS_PART_WATER AS CUST_PART_WATER, C.CUSTOMER_NAME AS CUSTOMER_NAME, LC.TRACE_IDS AS TRACE_IDS, R.IS_MAKE_BILL AS IS_MAKE_BILL FROM METER_RECORD R LEFT JOIN CUSTOMERS C ON R.CUSTOMER_ID = C.ID LEFT JOIN LOCATION_CUSTOMER LC ON R.CUSTOMER_ID = LC.CUSTOMER_ID
R.DELETED=0 AND C.DELETED=0 AND LC.DELETED=0
AND C.CHARGE_MODE = 1
AND R.CUSTOMER_ID = #{customerId}
AND R.PERIOD = #{period}
AND ( C.PROP_NAME LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR C.CUSTOMER_NAME LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR C.PROP_MOBILE LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR C.PROP_TEL LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR C.ROOM LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) )
AND R.CURR_DATE >= to_date(#{startDate}, 'yyyy-MM-dd HH24:Mi:ss')
AND R.CURR_DATE <= to_date(#{endDate}, 'yyyy-MM-dd HH24:Mi:ss')
AND LC.TRACE_IDS LIKE CONCAT(#{traceIds},'%')
AND R.READ_TYPE = #{readType}
AND R.OPERATOR_ID = #{operatorId}
AND R.IS_ADD_SUB_WATER = #{isAddSubWater}
AND R.IS_PART_WATER = #{isPartWater}
AND C.IS_PART_WATER = #{isPartWater}
UNION SELECT LISTAGG ( R.ID, ',' ) WITHIN GROUP ( ORDER BY R.CUSTOMER_ID, R.PERIOD, C.PROP_NAME ) AS RECORD_IDS, LISTAGG ( R.METER_ID, ',' ) WITHIN GROUP ( ORDER BY R.CUSTOMER_ID, R.PERIOD, C.PROP_NAME ) AS METER_IDS, R.CUSTOMER_ID AS CUSTOMER_ID, R.PERIOD AS PERIOD, R.IS_ADD_SUB_WATER AS IS_ADD_SUB_WATER, R.IS_PART_WATER AS IS_PART_WATER, C.IS_PART_WATER AS CUST_PART_WATER, C.CUSTOMER_NAME AS CUSTOMER_NAME, LC.TRACE_IDS AS TRACE_IDS, R.IS_MAKE_BILL AS IS_MAKE_BILL FROM METER_RECORD R LEFT JOIN CUSTOMERS C ON R.CUSTOMER_ID = C.ID LEFT JOIN LOCATION_CUSTOMER LC ON R.CUSTOMER_ID = LC.CUSTOMER_ID
R.DELETED=0 AND C.DELETED=0 AND LC.DELETED=0
AND C.CHARGE_MODE = 0
AND R.CUSTOMER_ID = #{customerId}
AND R.PERIOD = #{period}
AND ( C.PROP_NAME LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR C.CUSTOMER_NAME LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR C.PROP_MOBILE LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR C.PROP_TEL LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR C.ROOM LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) )
AND R.CURR_DATE >= to_date(#{startDate}, 'yyyy-MM-dd HH24:Mi:ss')
AND R.CURR_DATE <= to_date(#{endDate}, 'yyyy-MM-dd HH24:Mi:ss')
AND LC.TRACE_IDS LIKE CONCAT(#{traceIds},'%')
AND R.READ_TYPE = #{readType}
AND R.OPERATOR_ID = #{operatorId}
AND R.IS_ADD_SUB_WATER = #{isAddSubWater}
AND R.IS_PART_WATER = #{isPartWater}
AND C.IS_PART_WATER = #{isPartWater}
GROUP BY R.CUSTOMER_ID, R.PERIOD, C.CHARGE_MODE, R.IS_ADD_SUB_WATER, R.IS_PART_WATER, C.IS_PART_WATER, C.CUSTOMER_NAME, LC.TRACE_IDS, R.IS_MAKE_BILL ) RECORD ORDER BY RECORD.PERIOD DESC

SELECT
	SUM( CURR_READ ) 
FROM
	METER_RECORD 
WHERE
	
	DELETED = 0 
	AND CUSTOMER_ID = #{customerId} 
	AND METER_ID = #{meterId} 
	AND to_char ( to_date ( PERIOD, 'yyyy-mm' ), 'yyyy' ) = #{year}

SELECT P.PERIOD, P.WATER_AMOUNT, (I.CREDIT_AMOUNT-I.DEBIT_AMOUNT) WATER_FEE, LC.TRACE_IDS, P.CUSTOMER_ID, P.RECORD_ID, P.WATER_USE FROM CUSTOMER_ACCOUNT_ITEM I LEFT JOIN LOCATION_CUSTOMER LC ON I.CUSTOMER_ID = LC.CUSTOMER_ID LEFT JOIN PARTITION_WATER P ON I.ID = P.ACCOUNT_ITEM_ID WHERE 1=1 AND LC.DELETED=0
AND LC.TRACE_IDS LIKE CONCAT(#{traceIds}, '%')
AND I.DEBIT_CREDIT = #{debitCredit}
AND I.CREDIT_SUBJECT = #{creditSubject}
AND P.PERIOD=#{period}
AND NOT EXISTS (SELECT 1 FROM CUSTOMER_ACCOUNT_ITEM C WHERE C.ID=I.ID AND C.SETTLEMENT_STATUS=#{arrearsStatus}) ORDER BY I.ID DESC

SELECT P.*, (CA.CREDIT_AMOUNT-CA.DEBIT_AMOUNT) WATER_FEE
FROM customer_account_item CA LEFT JOIN PARTITION_WATER P ON CA.ID = P.ACCOUNT_ITEM_ID WHERE 1=1 AND CA.DELETED=0
AND CA.ID = #{accountItemId}
AND CA.DEBIT_CREDIT = #{debitCredit}
AND CA.CREDIT_SUBJECT = #{creditSubject}
AND NOT EXISTS (SELECT 1 FROM CUSTOMER_ACCOUNT_ITEM C WHERE C.ID=CA.ID AND C.SETTLEMENT_STATUS=#{arrearsStatus})

SELECT R.*, C.CUSTOMER_NAME, C.CUSTOMER_TYPE, LM.TRACE_IDS FROM meter_record R LEFT JOIN CUSTOMERS C ON R.CUSTOMER_ID = C.ID LEFT JOIN METERS M ON R.METER_ID = M.ID LEFT JOIN LOCATION_METER LM ON R.METER_ID=LM.METER_ID
R.DELETED=0 AND LM.DELETED!=1 AND C.DELETED=0 AND M.DELETED=0
AND R.PRE_READ IS NOT NULL
AND R.CUSTOMER_ID = #{customerId}
AND ( C.CUSTOMER_CODE LIKE CONCAT(CONCAT('%',#{searchCond}),'%')
OR C.CUSTOMER_NAME LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR C.CUSTOMER_MOBILE LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR C.CUSTOMER_TEL LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR C.ROOM LIKE CONCAT('%', CONCAT(#{searchCond}, '%'))
OR M.METER_NO LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR M.STEEL_SEAL_NO LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR M.DESCRIPTION LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) OR M.PLACE LIKE CONCAT('%', CONCAT(#{searchCond}, '%')) )
AND LM.TRACE_IDS LIKE CONCAT(#{traceIds},'%')
AND R.PERIOD=#{period}
AND R.CURR_DATE >= to_date(#{startDate}, 'yyyy-MM-dd HH24:Mi:ss')
AND R.CURR_DATE <= to_date(#{endDate}, 'yyyy-MM-dd HH24:Mi:ss')
AND R.READ_TYPE = #{readType}
AND R.STATUS = #{meterRecordStatus}
AND R.IS_PART_WATER = #{isPartWater}
AND R.OPERATOR_ID = #{operatorId}
AND R.CURR_AMOUNT > 0
AND R.CURR_AMOUNT=0
AND R.CURR_AMOUNT < 0
AND (R.CURR_AMOUNT > (select upper_bound from ABNORMAL_QUANTITY where type=1 ) or R.CURR_AMOUNT < (select lower_bound from ABNORMAL_QUANTITY where type=1))
ORDER BY R.PERIOD DESC, R.CUSTOMER_ID DESC, R.ID DESC

SELECT * FROM meter_record m
m.deleted=0
AND M.CUSTOMER_ID=#{customerId}
AND M.METER_ID=#{meterId}
and rownum = 1
ORDER BY m.CURR_DATE DESC

SELECT R.* FROM ( SELECT * FROM METER_RECORD WHERE DELETED=0
AND CUSTOMER_ID=#{customerId}
AND PERIOD=#{period}
AND METER_ID=#{meterId}
ORDER BY PERIOD DESC, ID DESC, CURR_DATE DESC ) R WHERE ROWNUM =1

SELECT * FROM meter_record M
M.deleted=0
AND M.CUSTOMER_ID=#{customerId}
AND M.PERIOD=#{period}
AND (M.CURR_AMOUNT > (select upper_bound from ABNORMAL_QUANTITY where type=1 ) or M.CURR_AMOUNT < (select lower_bound from ABNORMAL_QUANTITY where type=1))

SELECT * FROM METER_RECORD R LEFT JOIN CUSTOMERS C ON R.CUSTOMER_ID = C.ID LEFT JOIN LOCATION_METER LC ON R.METER_ID = LC.METER_ID
R.deleted=0 AND LC.DELETED!=1 AND R.PRE_READ IS NOT NULL AND R.CURR_AMOUNT IS NOT NULL
AND R.PERIOD = #{period}
AND LC.TRACE_IDS LIKE CONCAT(#{traceIds},'%')
AND R.CURR_DATE >= to_date(#{startDate}, 'yyyy-MM-dd HH24:Mi:ss')
AND R.CURR_DATE <= to_date(#{endDate}, 'yyyy-MM-dd HH24:Mi:ss')
AND R.READ_TYPE = #{readType}
AND R.IS_PART_WATER = #{isPartWater}
AND ( C.CUSTOMER_CODE LIKE concat(concat('%',#{searchCond}),'%') OR C.PROP_NAME LIKE concat(concat('%',#{searchCond}),'%') OR C.PROP_TEL LIKE concat(concat('%',#{searchCond}),'%') OR C.PROP_MOBILE LIKE concat(concat('%',#{searchCond}),'%') OR C.CUSTOMER_NAME LIKE concat(concat('%',#{searchCond}),'%') OR C.CUSTOMER_TEL LIKE concat(concat('%',#{searchCond}),'%') OR C.CUSTOMER_MOBILE LIKE concat(concat('%',#{searchCond}),'%') OR C.ROOM LIKE concat(concat('%',#{searchCond}),'%') )
ORDER BY R.PERIOD DESC, C.CUSTOMER_NAME DESC

SELECT * FROM METER_RECORD R
R.DELETED=0
AND R.PERIOD=#{period}
AND R.METER_ID=#{meterId}
AND R.PRE_READ IS NOT NULL AND R.CURR_AMOUNT IS NOT NULL
ORDER BY R.METER_ID

