mapper.select:
select T.* FROM (SELECT a.* , months_between ( add_months(a.check_date,s.DETECTION_PERIOD*12), sysdate ) as num FROM meters a LEFT JOIN SYS_CHECK_METER s ON s.CALIBER = a.CALIBER WHERE ( ( SELECT sysdate FROM DUAL ) > trunc ( add_months ( a.check_date, ( 12 * ( SELECT DETECTION_PERIOD FROM SYS_CHECK_METER b WHERE b.CALIBER = a.CALIBER ) ) - 2 ), 'DD' ) )
AND a.CALIBER=#{caliber}
AND a.CYCLE_STATUS IN
#{item}
AND ( a.METER_NO LIKE concat(concat('%',#{searchCond}),'%') OR a.PLACE LIKE concat(concat('%',#{searchCond}),'%') OR a.FACTORY LIKE concat(concat('%',#{searchCond}),'%') )
) T
( T.num >=0 and T.num < 1)
AND ( T.num >=1 and T.num <= 2)
AND T.num <0
ORDER BY T.CHECK_DATE DESC
